import * as React from 'react';
import * as S from './styled';
import { FaArrowLeft, FaSearch, FaBars, FaRegHeart, FaHeart } from 'react-icons/fa';

type Props = {
  icon: string;
  size: number;
  onClick?: () => void;
};

const findIcon = (iconName: string) => {
  switch (iconName) {
    case 'ARROW_LEFT':
      return <FaArrowLeft />;
    case 'SEARCH':
      return <FaSearch />;
    case 'BARS':
      return <FaBars />;
    case 'REG_HEART':
      return <FaRegHeart />;
    case 'HEART':
      return <FaHeart />;
    default:
      return;
  }
};

export const Icon = ({ icon, onClick, ...props }: Props) => {
  return (
    <S.Icon role={icon} onClick={onClick} {...props}>
      {findIcon(icon)}
    </S.Icon>
  );
};
